function  Venus(){
  return(
    <div className="container">
    <div className="description">
      <div className="sketch">
        <img src="./images/planet-venus.svg" alt="pic" className="earth1"/>
      </div>
      <div className="text">
        <h1>VENUS</h1>
        <div className="origin-text">
          <p>Venus is the second planet from the Sun. It is named after the Roman goddess of love and beauty. As the brightest natural object in Earth's night sky after the Moon, Venus can cast shadows and 
              can be, on rare occasions, visible to the naked eye in broad daylight.</p>
          <p>Source : Wikipedia<span><a href=" https://en.wikipedia.org/wiki/Venus"> <img src="./images/external-link.png" alt=""/></a> </span></p>
          <div className="buttons">
              <button className="btn btn01 active"><span>01</span> Overview</button> 
              <button className="btn btn02"><span>02</span>Internal Structure </button>
              <button className="btn btn03"><span>03</span>Surface Geology </button>
          </div>
        </div>
      </div>
    </div>
    <div className="parameter">
    <div className="boxing box-rotation">
              <p>Rotation Time</p>
              <h2 className="rotation">0.99 Days</h2>
            </div>

            <div className="boxing box-revolution">
              <p>Revolution Time</p>
              <h2 className="revolution">365.26 Days</h2>
            </div>

            <div className="boxing box-radius">
              <p>Radius</p>
              <h2 className="radius">6,371</h2>
            </div>

            <div className="boxing box-temp">
              <p>Average Temp.</p>
              <h2 className="temp">16°C</h2>
            </div>
    </div>

  </div>
)
}
