import React, { useState} from "react";
import {
    BrowserRouter as Router,
    Routes,
    Route,
    Link
} from 'react-router-dom';
import { Close } from '@mui/icons-material';
import { MenuOutlined } from '@mui/icons-material';
import Mercury from './Mercury';
import Venus from './Venus';
import Earth from './Earth';
import Mars from './Mars';
import Jupiter from './Jupiter';
import Saturn from './Saturn';
import Uranus from './Uranus';
import Neptune from './Neptune';
import './App.css';

function App(){
const [active, setActive] = useState(false);

const showMenu = () => {
    setActive(!active);
}

    return <Router>
    <div className="header">
            <div className="menu-icon">
                <MenuOutlined className="menu" onClick = {showMenu}/>
            </div>

        <nav className= {active ? "slider active" : "slider"}>
            <div className="closed" onClick = {showMenu}>
                <Close className="close" onClick = {showMenu}/>
            </div>
            <Link to='/brand' className="link brand">THE PLANETS</Link>
            <Link to='/' className="link">MERCURY</Link> 
            <Link to='/venus' className="link">VENUS</Link>
            <Link to='/earth' className="link">EARTH</Link>
            <Link to='/mars' className="link">MARS</Link> 
            <Link to='/jupiter' className="link">JUPITER</Link>
            <Link to='/saturn' className="link">SATURN</Link>
            <Link to='/uranus' className="link">URANUS</Link>
            <Link to='/neptune' className="link">NEPTUNE </Link>
        </nav>
        <Routes>
            <Route path='/brand' element={<Mercury/>}/>
            <Route path='/' element={<Mercury/>}/>
            <Route path='/venus' element={<Venus/>}/>
            {/* <Route path='/earth' element={<Earth/>}/>
            <Route path='/mars' element={<Mars/>}/> */}
            <Route path='/' element={<Jupiter/>}/>
            <Route path='/saturn' element={<Saturn/>}/>
            <Route path='/uranus' element={<Uranus/>}/>
            <Route path='/neptune' element={<Neptune/>}/>
        </Routes>
        </div>
    </Router>
};
export default App;
