import React, {useState } from "react";
import styled from "styled-components";
import data from "./data.json";
import ButtonGroup from "./ButtonGroup";
import "./App.css";

function  Earth(){

  const [isOver, setIsOver] = useState(true);
  const [isStruct, setIsStruct] = useState(false);
  const [isGeo, setIsGeo] = useState(false);
  // const [activeStyleOver, setActiveStyleOver] = useState([BtnStyle]);
  // const [activeStyleGeo, setActiveStyleGeo] = useState([Button]);
  // const [activeStyleStruct, setActiveStyleStruct] = useState([Button]);
  // const [styleBtn, setStyleBtn] = useState([Button, BtnStyle]);
  // const active= [Button, BtnStyle];

  function overview(){
    setIsOver(!isOver);
    setIsStruct(false);
    setIsGeo(false);
    // setActiveStyleOver(!activeStyleOver);
    // setActiveStyleOver([BtnStyle]);
  }

  function structure(){
    setIsOver(false);
    setIsStruct (!isStruct);
    setIsGeo(false);
    // setActiveStyleStruct(!activeStyleStruct);
    // setStyleBtn(![BtnStyle]);
    }

  function geology(){
    setIsOver(false);
    setIsStruct(false);
    setIsGeo(!isGeo);
    setActiveStyleGeo(!activeStyleGeo);
    // setActiveStyleGeo([BtnStyle]);
  }


  return(
<div>
  <main> 
    <div className={isOver ? "isOver" : null}>
        {isOver ? (
          <div>
            <img src={data[2].images.planet.toString()}  alt="pic" className="earth1"/>
            <div>
              <h1 className="name">{data[2].name} </h1>
              <p className="text">{data[2].overview.content} </p>
              <p className="text-source">Source : Wikipedia <a href={data[2].overview.source} target="_blank" rel="noreferrer"> <img src="./assets/external-link.png" alt="" className="reference"/></a>
              </p>
            </div>
          </div>) : null};
    </div>

    <div className= {isStruct ? "isStruct" : null}>
          {isStruct ? (
        <div>
            <img src={data[2].images.internal.toString()}  alt="pic" className="earth1"/>
          <div>
              <h1 className="name">{data[2].name} </h1>
              <p className="text">{data[2].structure.content} </p>
              <p className="text-source">Source : Wikipedia <a href={data[2].structure.source} target="_blank" rel="noreferrer"> <img src="./assets/external-link.png" alt="" className="reference"/></a>
              </p>
          </div> 
        </div>) : null};
    </div>

    <div className= {isGeo ? "isGeo" : null}>
          {isGeo ? (
        <div>
            <img src={data[2].images.planet.toString()}  alt="pic" className="earth1"/>
            <img src={data[2].images.geology.toString()} alt="" className="aplication"/>
          <div>
              <h1 className="name">{data[2].name} </h1>
              <p className="text">{data[2].geology.content} </p>
              <p className="text-source">Source : Wikipedia <a href={data[2].geology.source} target="_blank" rel="noreferrer"> <img src="./assets/external-link.png" alt="" className="reference"/></a>
              </p>
          </div> 
        </div>) : null};
    </div>


        <div className="buttons">
          <ButtonGroup/>
            {/* <Button onClick={overview}>01<span>Overview</span> </Button> 
            <Button  onClick={structure} >02<span>Internal Structure</span> </Button>
            <Button  onClick={geology}>03<span>Surface Geology</span> </Button> */}
        </div>
  </main>

      <footer>
      <div className="parameter">
      <div className="boxing box-rotation">
        <p>Rotation Time</p>
        <h2 className="rotation">{data[2].rotation} </h2>
      </div>

      <div className="boxing box-revolution">
        <p>Revolution Time</p>
        <h2 className="revolution">{data[2].revolution} </h2>
      </div>

      <div className="boxing box-radius">
        <p>Radius</p>
        <h2 className="radius">{data[2].radius} </h2>
      </div>

      <div className="boxing box-temp">
        <p>Average Temp.</p>
        <h2 className="temp">{data[2].temperature} </h2>
      </div>
    </div>
</footer>
</div>
    )
}
export default Earth;
