import React, {useState } from "react";
import styled from 'styled-components';
import data from "./data.json";
// import ButtonGroup from "./ButtonGroup";
import "./App.css";

const button = styled.button`
width: 350px;
height: 48px;
margin-bottom: 16px;
padding-left: 28px;

background-color: #231d2b;
color:white;
font-family: 'Spartan', sans-serif;
font-weight: 700;
font-size: 12px;
line-height: 25px;
letter-spacing: 2.57143px;
text-transform: uppercase;
cursor: pointer;
text-align: left;
border: 1px solid rgba(255, 255, 255, .2);
`;

const btnStyle = styled.button`
width: 350px;
height: 48px;
margin-bottom: 16px;
padding-left: 28px;
color:white;
font-family: 'Spartan', sans-serif;
font-weight: 700;
font-size: 12px;
line-height: 25px;
letter-spacing: 2.57143px;
text-transform: uppercase;
cursor: pointer;
text-align: left;
border: 1px solid rgba(255, 255, 255, .2);

background-color: {data[1].colorit};
`;


function  Venus(){

  const [isOver, setIsOver] = useState(true);
  const [isStruct, setIsStruct] = useState(false);
  const [isGeo, setIsGeo] = useState(false);
  const [activeStyleOver, setActiveStyleOver] = useState([button]);
  const [styleBtn, setStyleBtn] = useState([button, btnStyle])

  function overview(){
    setIsOver(!isOver);
    setIsStruct(false);
    setIsGeo(false);
    setActiveStyle(!activeStyle);
  }

  function structure(){
    setIsOver(false);
    setIsStruct (!isStruct);
    setIsGeo(false);
    setActiveStyle(btnStyle);
    setStyleBtn(btnStyle);
    }

  function geology(){
    setIsOver(false);
    setIsStruct(false);
    setIsGeo(!isGeo);
    setActiveStyle(!activeStyle);
  }


  return(
<div>
  <main> 
    <div className={isOver ? "isOver" : null}>
        {isOver ? (
          <div>
            <img src={data[1].images.planet.toString()}  alt="pic" className="mercury1"/>
            <div>
              <h1 className="name">{data[1].name} </h1>
              <p className="text">{data[1].overview.content} </p>
              <p className="text-source">Source : Wikipedia <a href={data[1].overview.source} target="_blank" rel="noreferrer"> <img src="./assets/external-link.png" alt="" className="reference"/></a>
              </p>
            </div>
          </div>) : null};
    </div>

    <div className= {isStruct ? "isStruct" : null}>
          {isStruct ? (
        <div>
            <img src={data[1].images.internal.toString()}  alt="pic" className="mercury1"/>
          <div>
              <h1 className="name">{data[1].name} </h1>
              <p className="text">{data[1].structure.content} </p>
              <p className="text-source">Source : Wikipedia <a href={data[1].structure.source} target="_blank" rel="noreferrer"> <img src="./assets/external-link.png" alt="" className="reference"/></a>
              </p>
          </div> 
        </div>) : null};
    </div>

    <div className= {isGeo ? "isGeo" : null}>
          {isGeo ? (
        <div>
            <img src={data[1].images.planet.toString()}  alt="pic" className="mercury1"/>
            <img src={data[1].images.geology.toString()} alt="" className="aplication"/>
          <div>
              <h1 className="name">{data[1].name} </h1>
              <p className="text">{data[1].geology.content} </p>
              <p className="text-source">Source : Wikipedia <a href={data[1].geology.source} target="_blank" rel="noreferrer"> <img src="./assets/external-link.png" alt="" className="reference"/></a>
              </p>
          </div> 
        </div>) : null};
    </div>


        <div className="buttons">
          {/* <ButtonGroup/> */}
            <button className="btn" onClick={overview} style = {{ activeStyle} ? {setActiveStyle} : {activeStyle} }>01<span>Overview</span> </button> 
            <button className="btn `{${styleBtn} ? {setStyleBtn} :  {styleBtn}`}" onClick={structure} style= {{ activeStyle} ? {activeStyle} :  {setActiveStyle}}>02<span>Internal Structure</span> </button>
            <button className="btn" onClick={geology} style={{activeStyle} ? {setActiveStyle} :  {activeStyle}}>03<span>Surface Geology</span> </button>
        </div>
  </main>

      <footer>
      <div className="parameter">
      <div className="boxing box-rotation">
        <p>Rotation Time</p>
        <h2 className="rotation">{data[1].rotation} </h2>
      </div>

      <div className="boxing box-revolution">
        <p>Revolution Time</p>
        <h2 className="revolution">{data[1].revolution} </h2>
      </div>

      <div className="boxing box-radius">
        <p>Radius</p>
        <h2 className="radius">{data[1].radius} </h2>
      </div>

      <div className="boxing box-temp">
        <p>Average Temp.</p>
        <h2 className="temp">{data[1].temperature} </h2>
      </div>
    </div>
</footer>
</div>
    )
}
export default Venus;
