  import { React, useState } from "react";
  import "./App.css";
  import data from "./data";

  function Earth(){
    const [activeState, setActiveState] = useState(false);
    const dataEarth = data.filter(item => item.name === "Earth");
    console.log(dataEarth[0].structure);
    console.log(dataEarth[0].geology.content)


    const btn1 = document.querySelector(".btn01");
    const btn2 = document.querySelector(".btn02");
    const source = document.querySelector(".source");
    const earth1 = document.querySelector(".earth1");
    const btns = document.querySelectorAll(".btn");

    const btn3 = document.querySelector(".btn03");
    const pic = document.querySelector(".earth1");
    const content = document.querySelector(".p-text");

    function updateDataTwo(){
      content.innerText = `${dataEarth[0].structure.content}`;
      pic.setAttribute('src', './images/planet-earth-i')
    }
    // image.setAttribute('src', 'fantasy.jpg');    // btn3.addEventListener('click', ()=>{
    //   content.innerHTML = dataEarth[0].geology.content;
    // })

    // btn2.addEventListener("click", () =>{
    //   content.innerText = `${dataEarth[0].structure.content}`;
    // })

    // console.log( dataEarth.structure.content)

    const btnStyle = {
      backgroundColor: `${dataEarth.colorit}`
    }


    return(
      <div className="container">
        <div className="description">
          <div className="sketch">
            <img src="./images/earth1.png" alt="pic" className="earth1"/>
          </div>
          <div className="text">
            <h1>EARTH</h1>
            <div className="origin-text">
              <p className="p-text">
                {`${dataEarth[0].overview.content}`}
                {/* Third planet from the Sun and the only known planet to harbor life. About 29.2% of Earth's surface is land with remaining 70.8% is covered with water. Earth's distance from the Sun, physical properties 
                and geological history have allowed life to evolve and thrive. */}
              </p>
              <p>Source : Wikipedia<span><a href=" https://en.wikipedia.org/wiki/Earth" className="source"> <img src="./images/external-link.png" alt=""/></a> </span></p>
                  <div className="buttons">
                    <button className="btn btn01 active" style={btnStyle}><span>01</span> Overview</button> 
                    <button onClick={updateDataTwo} className= " btn02 = activeState ? {`${btnStyle}`} : btn"><span>02</span>Internal Structure </button>
                    {/* <button className= "btn btn02" ><span>02</span>Internal Structure </button> */}
                    <button className="btn btn03"><span>03</span>Surface Geology </button>
                  </div>
              </div>
          </div>
        </div>

        <div className="parameter">
            <div className="boxing box-rotation">
              <p>Rotation Time</p>
              <h2 className="rotation">0.99 Days</h2>
            </div>

            <div className="boxing box-revolution">
              <p>Revolution Time</p>
              <h2 className="revolution">365.26 Days</h2>
            </div>

            <div className="boxing box-radius">
              <p>Radius</p>
              <h2 className="radius">6,371</h2>
            </div>

            <div className="boxing box-temp">
              <p>Average Temp.</p>
              <h2 className="temp">16Â°C</h2>
            </div>
        </div>

      </div>
    )
  }
export default Earth;
