import  React, { useState } from "react";
import styled from "./App.css"
import data from "./data.json";




function ButtonGroup(){
    const [active, setActive] = useState(types[0]);
    const Button = styled.button
    `width: 350px;
    height: 48px;
    margin-bottom: 16px;
    padding-left: 28px;
    
    background-color: #231d2b;
    color:white;
    font-family: 'Spartan', sans-serif;
    font-weight: 700;
    font-size: 12px;
    line-height: 25px;
    letter-spacing: 2.57143px;
    text-transform: uppercase;
    cursor: pointer;
    text-align: left;
    border: 1px solid rgba(255, 255, 255, .2);
    
    &:disabled {
        backgroundColor: {data[1].colorit};
        cursor: default;
    }`;
    
    
     const ButtonToggle = styled(Button)`
    background-color: #231d2b;
      ${({ active }) =>
        active &&
        `
        backgroundColor: {data[1].colorit};
      `}
    `;
    const types = ["01  Overview", "02   Internal Structure", "03   Surface Geology"];
    

    return (
        types.map(type => (
          <ButtonToggle
            key={type}
            active={active === type}
            onClick={() => setActive(type)}
          >
            {type}
          </ButtonToggle>
        ))
    );
}
export default ButtonGroup;
