import { React, useState } from "react";
import data from "./data.json";
import colorits from "./colorits";
import "./App.css";

function  Mercury(){
  const [isData, setIsData] = useState({data});
  const [smena, setSmena] = useState(false);

  const activStyle = {
    // if (data[index].name === colorits[index].name)
    backgroundColor: `${data[0].colorit}`
  }


function structure(){
setIsData({
  ...data,
  images: data[0].images.internal,
  overview: data[0].structure,
  name: data[5].name
}
)
console.log(setIsData)

}

  return(
  <div>
      <main>
          <div className="">
            <img src={data[0].images.planet.toString()}  alt="pic" className="mercury1"/>

            <div>
              <h1 className="name">{data[0].name} </h1>
              <p className="text">{data[0].overview.content} </p>
              <p className="text-source">Source : Wikipedia <a href={data[0].overview.source} target="_blank" rel="noreferrer"> <img src="./assets/external-link.png" alt="" className="reference"/></a>
              </p>
            </div> 
          </div>
              <div className="buttons">
                  <button className="btn btn01 active">01<span>Overview</span> </button> 
                  <button className="btn btn02" onClick={structure}>02<span>Internal Structure</span> </button>
                  <button className="btn btn03">03<span>Surface Geology</span> </button>
              </div>
      </main>

      <footer>
      <div className="parameter">
      <div className="boxing box-rotation">
        <p>Rotation Time</p>
        <h2 className="rotation">0.99 Days</h2>
      </div>

      <div className="boxing box-revolution">
        <p>Revolution Time</p>
        <h2 className="revolution">365.26 Days</h2>
      </div>

      <div className="boxing box-radius">
        <p>Radius</p>
        <h2 className="radius">6,371</h2>
      </div>

      <div className="boxing box-temp">
        <p>Average Temp.</p>
        <h2 className="temp">16°C</h2>
      </div>
    </div>
</footer>
</div>
    )
}
export default Mercury;
