  import React, {useState } from "react";
  import styled from "styled-components";
  import data from "./data.json";
  import "./App.css";

  const Button = styled.button`
  width: 350px;
  height: 48px;
  margin-bottom: 16px;
  padding-left: 28px;

  background-color: rgb(35, 29, 43);
  color:white;
  font-family: 'Spartan', sans-serif;
  font-weight: 700;
  font-size: 12px;
  line-height: 25px;
  letter-spacing: 2.57143px;
  text-transform: uppercase;
  cursor: pointer;
  text-align: left;
  border: 1px solid rgba(255, 255, 255, .2);
  `;

  const ButtonToggle = styled(Button)`
  background-color: rgb(35, 29, 43);
  ${({ active }) =>
    active &&
    `
    background-color: ${data[3].colorit};
    `}
`; 

function  Mars(){
  const btn = {
    backgroundColor: "#231d2b"
  }
  const btnStyle = {
    backgroundColor: `${data[0].colorit}`
  }
  const active = [btn, btnStyle];

    const [isOver, setIsOver] = useState(true);
    const [isStruct, setIsStruct] = useState(false);
    const [isGeo, setIsGeo] = useState(false);
    const [activeStyle, setActiveStyle] = useState([btn]);

    // const types = ["01  Overview", "02   Internal Structure", "03   Surface Geology"];
    // const [active, setActive] = useState(types[0]);

// function myvisit(){
//   setActive(types);
//   if (setActive(!types[0])) {
//     setIsOver(!isOver);
//     setIsStruct(false);
//     setIsGeo(false);
//   }
//   else if (active(!types[1])) {
//     setIsOver(false);
//     setIsStruct (!isStruct);
//     setIsGeo(false);

//   }
// else if (setActive(!types[1])) {
//   setIsOver(false);
//   setIsStruct(false);
//   setIsGeo(!isGeo);
// }
// }

    function overview(){
      setIsOver(!isOver);
      setIsStruct(false);
      setIsGeo(false);
    }

    function structure(){
      setIsOver(false);
      setIsStruct (!isStruct);
      setIsGeo(false);
      }

    function geology(){
      setIsOver(false);
      setIsStruct(false);
      setIsGeo(!isGeo);
    }


    return(
  <div>
    <main> 
      <div className={isOver ? "isOver" : null}>
          {isOver ? (
            <div className="image-planets">
              <div>
              <img src={data[3].images.planet.toString()}  alt="pic" className="mars1"/>
              </div>
              <div className="whole-text">
                <h1 className="name">{data[3].name} </h1>
                <p className="text">{data[3].overview.content} </p>
                <p className="text-source">Source : Wikipedia <a href={data[3].overview.source} target="_blank" rel="noreferrer"> <img src="./assets/external-link.png" alt="" className="reference"/></a>
                </p>
              </div>
            </div>) : null};
      </div>

      <div className= {isStruct ? "isStruct" : null}>
            {isStruct ? (
          <div className="image-planets">
            <div>
              <img src={data[3].images.internal.toString()}  alt="pic" className="mars1"/>
              </div>
            <div className="whole-text">
                <h1 className="name">{data[3].name} </h1>
                <p className="text">{data[3].structure.content} </p>
                <p className="text-source">Source : Wikipedia <a href={data[3].structure.source} target="_blank" rel="noreferrer"> <img src="./assets/external-link.png" alt="" className="reference"/></a>
                </p>
            </div> 
          </div>) : null};
      </div>

      <div className= {isGeo ? "isGeo" : null}>
            {isGeo ? (
          <div className="image-planets">
            <div>
              <img src={data[3].images.planet.toString()}  alt="pic" className="mars1"/>
              <img src={data[3].images.geology.toString()} alt="" className="aplication"/>
              </div>
              <div className="whole-text">
                <h1 className="name">{data[3].name} </h1>
                <p className="text">{data[3].geology.content} </p>
                <p className="text-source">Source : Wikipedia <a href={data[3].geology.source} target="_blank" rel="noreferrer"> <img src="./assets/external-link.png" alt="" className="reference"/></a>
                </p>
            </div> 
          </div>) : null};
      </div>

      {/* <div className="buttons">
                  <button className="btn" onClick={overview} style={btnStyle ? btnStyle : btn} >01<span>Overview</span> </button> 
                  <button className="btn"  onClick={structure} style ={activeStyle === btn ? btnStyle : btn}>02<span>Internal Structure</span> </button>
                  <button className="btn" onClick={geology} style={activeStyle=== btn ? {setActiveStyle} :  {activeStyle}}>03<span>Surface Geology</span> </button>
              </div> */}
          <div className="buttons">
          {types.map((type) => (
          <ButtonToggle
            key={type}
            active={active === type}
            onClick={()=> setActive(type)}
            // onClick = { myvisit}
            >
            {type}
          </ButtonToggle>
        ))}
          </div>
    </main>

        <footer>
        <div className="parameter">
        <div className="boxing box-rotation">
          <p>Rotation Time</p>
          <h2 className="rotation">{data[1].rotation} </h2>
        </div>

        <div className="boxing box-revolution">
          <p>Revolution Time</p>
          <h2 className="revolution">{data[1].revolution} </h2>
        </div>

        <div className="boxing box-radius">
          <p>Radius</p>
          <h2 className="radius">{data[1].radius} </h2>
        </div>

        <div className="boxing box-temp">
          <p>Average Temp.</p>
          <h2 className="temp">{data[1].temperature} </h2>
        </div>
      </div>
  </footer>
  </div>
      )
  }
  export default Mars;
