import { React, useState } from "react";
import data from "./data.json";
import "./App.css";

  function Jupiter(){
    const btn = {
      backgroundColor: "#231d2b"
    }
    const btnStyle = {
      backgroundColor: `${data[4].colorit}`
    }
    const active = [btn, btnStyle];

    const [isOver, setIsOver] = useState(true);
    const [isStruct, setIsStruct] = useState(false);
    const [isGeo, setIsGeo] = useState(false);
    const [activeStyle, setActiveStyle] = useState([btn]);

    function structure(){
      setIsOver(false);
      setIsStruct (!isStruct);
      setIsGeo(false);
      setActiveStyle(btnStyle);
      }
    function overview(){
      setIsOver(!isOver);
      setIsStruct(false);
      setIsGeo(false);
      setActiveStyle(!activeStyle);
    }

    function geology(){
      setIsOver(false);
      setIsStruct(false);
      setIsGeo(!isGeo);
      setActiveStyle(!activeStyle);
    }

    return(
      <div>
      <main> 

          <div className={isOver ? "isOver" : null}>
            {isOver ? (
              <div>
            <img src={data[4].images.planet.toString()}  alt="pic" className="mercury1"/>

            <div>
              <h1 className="name">{data[4].name} </h1>
              <p className="text">{data[0].overview.content} </p>
              <p className="text-source">Source : Wikipedia <a href={data[0].overview.source} target="_blank" rel="noreferrer"> <img src="./assets/external-link.png" alt="" className="reference"/></a>
              </p>
            </div>
            </div>) : null};
          </div>
          <div className= {isStruct ? "isStruct" : null}>
            {isStruct ? (
              <div>
            <img src={data[0].images.internal.toString()}  alt="pic" className="mercury1"/>

            <div>
              <h1 className="name">{data[0].name} </h1>
              <p className="text">{data[0].structure.content} </p>
              <p className="text-source">Source : Wikipedia <a href={data[0].structure.source} target="_blank" rel="noreferrer"> <img src="./assets/external-link.png" alt="" className="reference"/></a>
              </p>
            </div> 
            </div>) : null};
          </div>

          <div className= {isGeo ? "isGeo" : null}>
            {isGeo ? (
              <div>
            <img src={data[0].images.planet.toString()}  alt="pic" className="mercury1"/>
            <img src={data[0].images.geology.toString()} alt="" className="aplication"/>
            <div>
              <h1 className="name">{data[0].name} </h1>
              <p className="text">{data[0].geology.content} </p>
              <p className="text-source">Source : Wikipedia <a href={data[0].geology.source} target="_blank" rel="noreferrer"> <img src="./assets/external-link.png" alt="" className="reference"/></a>
              </p>
            </div> 
            </div>) : null};
          </div>


              <div className="buttons">
                  <button className="btn" onClick={overview} style={btnStyle ? btnStyle : btn} >01<span>Overview</span> </button> 
                <button className="btn"  onClick={structure} style ={activeStyle === btn ? btnStyle : btn}>02<span>Internal Structure</span> </button>
                  <button className="btn" onClick={geology} style={activeStyle=== btn ? {setActiveStyle} :  {activeStyle}}>03<span>Surface Geology</span> </button>
              </div>
      </main>

      <footer>

<div className="parameter">
      <div className="boxing box-rotation">
              <p>Rotation Time</p>
              <h2 className="rotation">0.99 Days</h2>
            </div>

            <div className="boxing box-revolution">
              <p>Revolution Time</p>
              <h2 className="revolution">365.26 Days</h2>
            </div>

            <div className="boxing box-radius">
              <p>Radius</p>
              <h2 className="radius">6,371</h2>
            </div>

            <div className="boxing box-temp">
              <p>Average Temp.</p>
              <h2 className="temp">16°C</h2>
            </div>
      </div>
  </footer>
    </div>
  )
  }
  export default Jupiter;

  // <!-- Overview Content Start -->

  // Jupiter is the fifth planet from the Sun and the largest in the Solar System. 
  // It is a gas giant with a mass two and a half times that of all the other 
  // planets in the Solar System combined, but less than one-thousandth the mass 
  // of the Sun.

  // Source : Wikipedia <!-- URL: https://en.wikipedia.org/wiki/Jupiter -->

  // <!-- Overview Content End -->

  // <!-- Internal Structure Content Start -->

  // When the Juno arrived in 2016, it found that Jupiter has a very diffuse core 
  // that mixes into its mantle. A possible cause is an impact from a planet of 
  // about ten Earth masses a few million years after Jupiter's formation, which 
  // would have disrupted an originally solid Jovian core.

  // Source : Wikipedia <!-- URL: https://en.wikipedia.org/wiki/Jupiter#Internal_structure -->

  // <!-- Internal Structure Content End -->

  // <!-- Surface Geology Content Start -->

  // The best known feature of Jupiter is the Great Red Spot, a persistent 
  // anticyclonic storm located 22° south of the equator. It is known to have 
  // existed since at least 1831, and possibly since 1665.

  // Source : Wikipedia <!-- URL: https://en.wikipedia.org/wiki/Jupiter#Great_Red_Spot_and_other_vortices -->

  // <!-- Surface Geology Content End -->

  // 01 Overview
  // 02 Internal Structure
  // 03 Surface Geology

  // Rotation Time
  // 9.93 Hours

  // Revolution Time
  // 11.86 Years

  // Radius
  // 69,911 KM

  // Average Temp.
  // -108°c
