  import { React, useState } from "react";
  import "./App.css";
  import data from "./data";

  function Earth(){
    const [isVisible, setIsVisible] = useState(false);
    const [isOver, setIsOver] = useState(false);
    const [ isStruct, setIsStruct] = useState(false);
    const [ isGeology, setIsGeologyi] = useState(false);

    const dataEarth = data.filter(item => item.name === "Earth");
    // console.log(dataEarth[0].structure);
    // console.log(dataEarth[0].geology.content)
    // console.log(dataEarth[0].images.planet);

    function Overview(){
      setIsVisible(!isVisible);
      setIsOver(!isOver);
    }


    const btnStyle = {
      backgroundColor: `${dataEarth.colorit}`
    }


    return(
      <div className="container">
        <div className="description">
          <div className="sketch"  className={isVisible ? null :"isVisible"}>
            {isVisible ? 
            <img src={`${data[2].images.planet}`} alt="pic" className="earth1"/> : null  }
            {/* "./images/earth1.png"  */}
          </div>
          <div className="text" className={isVisible ? null : "isVisi"} >
            <h1>{ `${data[2].name}`}</h1>
            <div className="origin-text" >
              <p className={isOver ? isOver : null}>
                {`${data[2].overview.content}`}
              </p>
              <p>Source : Wikipedia<span><a href= {`${data[2].overview.source}`} className="source" target="_blanck"> <img src="./images/external-link.png" alt=""/></a> </span></p>
              {/* " https://en.wikipedia.org/wiki/Earth"  */}
                  <div className="buttons">
                    <button onClick={Overview} className="btn overview active" style={btnStyle}><span>01</span> Overview</button> 
                    {/* <button onClick={updateDataTwo} className= " btn02 = activeState ? {`${btnStyle}`} : btn"><span>02</span>Internal Structure </button> */}
                    <button className= "btn structure" ><span>02</span>Internal Structure </button>
                    <button className="btn geology"><span>03</span>Surface Geology </button>
                  </div>
              </div>
          </div>
        </div>

        <div className="parameter">
            <div className="boxing box-rotation">
              <p>Rotation Time</p>
              <h2 className="rotation">0.99 Days</h2>
            </div>

            <div className="boxing box-revolution">
              <p>Revolution Time</p>
              <h2 className="revolution">365.26 Days</h2>
            </div>

            <div className="boxing box-radius">
              <p>Radius</p>
              <h2 className="radius">6,371</h2>
            </div>

            <div className="boxing box-temp">
              <p>Average Temp.</p>
              <h2 className="temp">16Â°C</h2>
            </div>
        </div>

      </div>
    )
  }
export default Earth;
